"use strict";function getBitrate(rate=160){return rate>=8?rate/8:20}function getAudioLength(time){try{if("string"==typeof time){const params=time.split(":"),count=params.length;if(2===count){return 60*params[0]+1*params[1]}if(3===count){return 3600*params[0]+60*params[1]+1*params[2]}return-1}return-1}catch(exp){return-1}}function getFileSize(time,rate=160){const audioLength=getAudioLength(time);return audioLength>-1?audioLength*getBitrate(rate):-1}module.exports={getFileSize:getFileSize};